contract RealGoodOffer is ERC777
{
    RealGoodFactory public immutable _factory;

    constructor(RealGoodFactory fac_)  ERC777("Real Good Offer","RGO",new address[](0)){
        _factory = fac_;
    }
}

abstract contract RealGoodFactory  {

    function registerCategory(string memory category_, string memory symbol, uint granularity_) public virtual returns (uint256 categoryid_);
    function getCategory(string memory category_) public view virtual returns (uint256 categoryid_);

    struct TokenInfo
    {
        uint    granularity;
        uint256 totalSupply;
        string  name;
        string  symbol;
    }
    function getCategoryInfo(uint256 categoryid_) public view virtual returns (TokenInfo memory);

    // seller
    function calculateDepositPerUnit(uint256 categoryid_, uint256 maxquote_) public virtual returns (uint256 value_);
    function mint(uint256 categoryid_, uint256 qty_,uint256 maxquote_) public virtual payable;
    function burn(uint256 categoryid_, uint256 qty_) public virtual;
    function setSellerCategoryMaxQuote(uint256 categoryid_, uint256 maxcurrencyquote_) public virtual payable;
    function setSellerCategoryDescription(uint256 categoryid_, string memory uridescription_) public virtual ; // description is specific to msg.sender
    function setSellerCategoryKeepingFee(uint256 categoryid_, uint256 keepingfee_) public virtual ; // description is specific to msg.sender, it will be applied to new trades
    function setSellerCategoryFreeKeeping(uint256 id_, uint blockcount_) public virtual ; // description is specific to msg.sender, it will be applied to new trades

    // initial buyer



    // current owner
    function launchDelivery(uint256 tradeid_) public virtual;
    function addKeepingFeeWallet(uint256 tradeid_,uint256 value_) public virtual payable;
    function getTradeExpirationTime(uint256 tradeid_) public virtual;
    function getTradeState(uint256 tradeid_) public virtual;


    function getTradeInfo(uint256 tradeid_) public virtual;



    // public
    function balanceOf(uint256 id_, address owner_) public view virtual returns (uint256);


    function tokenURI(uint256 id_) public view virtual returns (string memory);
    function getUserFame(address user_) public virtual returns (int32);


